mapper.select:
SELECT id, name, dept_code, rank_code
FROM member
WHERE id = #{id}

mapper.update:
UPDATE approval_doc
SET status = #{status}
WHERE seq = #{approvalId}

UPDATE approval_line
SET status = 'Y'
WHERE approval_id = #{approvalId}
  AND approver_id = #{userId}

UPDATE approval_line
SET status = 'R',
   reject_reason = #{reason}
WHERE approval_id = #{approvalId}
  AND approver_id = #{userId}

mapper.select:
SELECT DISTINCT D.SEQ, D.WRITER, D.TITLE, D.STATUS, D.WRITEDATE FROM approval_doc D /* ✅ 기안자 정보 JOIN (writer = name) */ JOIN member M ON D.WRITER = M.name
/* ✅ 결재라인 (숫자인 결재문서만 조인) */ LEFT JOIN approval_line L ON REGEXP_LIKE(L.APPROVAL_ID, '^[0-9]+$') AND TO_NUMBER(L.APPROVAL_ID) = D.SEQ
WHERE M.id = #{_parameter} OR L.APPROVER_ID = #{_parameter}
ORDER BY D.SEQ DESC
