mapper.select:
SELECT * FROM member WHERE 1=1
AND status = #{status}
AND dept_code = #{dept}
AND employmentType = #{employment}
AND rank_code = #{rank}
AND job_code = #{job}
ORDER BY CASE rank_code WHEN 'J001' THEN 1 WHEN 'J002' THEN 2 WHEN 'J003' THEN 3 WHEN 'J004' THEN 4 WHEN 'J005' THEN 5 WHEN 'J006' THEN 6 WHEN 'J007' THEN 7 WHEN 'J008' THEN 8 WHEN 'J009' THEN 9 END desc
