mapper.select:
SELECT NVL(userLeave, 0)
FROM leave_balance
WHERE id = #{memberId}

mapper.insert:
INSERT INTO leave_request (
  seq, member_id, leave_count, leave_code,
  start_leave_time, end_leave_time, reason, status, approval_id
) VALUES (
  leave_request_seq.NEXTVAL,
  #{member_id}, #{leave_count}, #{leave_code},
  #{start_leave_time}, #{end_leave_time}, #{reason}, #{status}, #{approval_id}
)

mapper.update:
UPDATE LEAVE_REQUEST
SET STATUS = #{status}
WHERE SEQ = #{seq}

mapper.select:
SELECT lr.seq, lr.member_id AS memberId, m.name AS memberName, m.rank_code AS rankCode, lr.leave_code AS leaveCode, lr.reason AS reason, lr.status AS status, lr.approval_id AS approvalId, TO_CHAR(lr.start_leave_time, 'YYYY-MM-DD HH24:MI:SS') AS startLeaveTime, TO_CHAR(lr.end_leave_time, 'YYYY-MM-DD HH24:MI:SS') AS endLeaveTime, lr.reject_reason AS rejectReason, TO_CHAR(lr.reject_time, 'YYYY-MM-DD HH24:MI:SS') AS rejectTime FROM leave_request lr JOIN member m ON lr.member_id = m.id
AND ( lr.member_id = #{memberId} OR EXISTS ( SELECT 1 FROM approval_line al WHERE al.approval_id = lr.approval_id AND al.approver_id = #{memberId}
) )
ORDER BY lr.start_leave_time DESC

SELECT LEAVE_COUNT
FROM LEAVE_REQUEST
WHERE SEQ = #{seq}

SELECT member_id
FROM leave_request
WHERE seq = #{seq}
