mapper.delete:
	    DELETE FROM TaskGroupMember
	    WHERE group_seq = #{group_seq}
      AND member_id = #{member_id}

mapper.insert:

  SELECT TASK_SEQ.NEXTVAL FROM DUAL


INSERT INTO TASK (
  seq, group_seq, title, description, assignee_id, status, created_id, created_at
) VALUES (
  #{seq}, #{group_seq}, #{title}, #{description}, #{assignee_id}, #{status}, #{created_id}, SYSDATE
)

	 INSERT INTO TASK_COMMENT (
  seq, task_seq, writer_id, writer_name, content, created_at
) VALUES (
  TASK_COMMENT_SEQ.nextval, #{task_seq}, #{writer_id},#{writer_name}, #{content}, sysdate 
)
